import Vue from 'vue';
import Vuex from 'vuex'


Vue.use(Vuex);

export const store=new Vuex.Store({
    state:{
        members:[
            {name:'Apple',price:20},
            {name:'Mango',price:40}
        ]
    },
    actions: {
        created(){
            this.$http.get('/user/?_sort=id&_order=desc')



            this.$http.get('/students/?_sort=id&_order=desc').then(function(data){
                       console.log(data.body);
                       this.members=data.body
                       this.$emit('stu',this.members)
                       console.log(this.members)
                   })
                }
            }
        })
