import Vue from 'vue';
import Vuex from 'vuex'
import axios from 'axios'

Vue.use(Vuex);

export const store=new Vuex.Store({
    state:{
        members:[
            {name:'Apple',price:20},
            {name:'Mango',price:40}
        ]
    },

    getters:{
        allmembers:(state)=>state.members
    },
    mutations:{
        getmembers:(state,payload)=>{
            state.members=payload;
        }
    },
    actions:{
        async getmembers({commit}){
            const response = axios.get('http://localhost:3000/user') 
            commit('getmembers',this.response.data)
        }
        }
        })
