import Vue from 'vue';
import Vuex from 'vuex'
import axios from 'axios'

Vue.use(Vuex);

export const store=new Vuex.Store({
    state:{
        members:[]
    },

    getters:{
        //allmembers:(state)=>state.members
        allmembers(state){
            console.log("done")
            return state.members
        }
    },
    mutations:{
        getmembers(state,payload){
            state.members=payload;
            console.log(state.members)
            return(state.members);

        },
        /*getmembers:(state,payload)=>{
            state.members=payload;
        }*/
        postmembers(state,payload){
            console.log("Payload : "+ JSON.stringify(payload))

            state.members.unshift(payload);            
        },
        putmembers(state,cid,payload){
            console.log("Payload : "+ JSON.stringify(payload))
            let ind =  state.members.findIndex(members=>members.id==cid)
            state.members.splice(ind,1,payload);

        }



    },
    actions:{
        getmembers:(context)=>{
           axios.get('http://localhost:3000/user')
            .then(
                (data)=>{
                console.log(data.data)
                context.commit('getmembers',data.data)
            })
        },
        postmembers:(context,payload)=>{
            axios.post('http://localhost:3000/user',payload)
            .then(
                data =>{
                    console.log(data.data)
                    context.commit('postmembers',data.data)
                }
            )
        },
        putmembers:(context,{cid,payload})=>{
            axios.put('http://localhost:3000/user/'+cid,payload)
            .then(
                data=>{
                    console.log(data)
                    let res =data.data
                    context.commit('putmembers',{ cid,res})
                }
            )
        }

    }

})
