import Vue from 'vue';
import Vuex from 'vuex'


Vue.use(Vuex);

export const store=new Vuex.Store({
    state:{
        members:[
            {name:'Apple',price:20},
            {name:'Mango',price:40}
        ]
    },

    getters:{
        allmembers:(state)=>state.members
    },
    mutations:{
        getmembers:(state,payload)=>{
            state.members=payload;
        }
    },
    actions:{
        getmembers:(context,payload)=>{
            context.commit('getmembers',payload)
        }
        }
        })
